INSERT INTO `tl_faq` (`id`, `pid`, `sorting`, `tstamp`, `question`, `alias`, `author`, `answer`, `addImage`, `singleSRC`, `alt`, `size`, `imagemargin`, `imageUrl`, `fullsize`, `caption`, `floating`, `addEnclosure`, `enclosure`, `noComments`, `published`, `overwriteMeta`, `imageTitle`, `orderEnclosure`) VALUES(1, 1, 128, 1568210069, 'Was ist der Unterschied zwischen Honigbienen und Wildbienen?', 'was-ist-der-unterschied-zwischen-honigbienen-und-wildbienen', 1, '<p><strong>Honigbienen leben in einem großen Volk</strong></p>\n<p>Honigbienen leben in einem großen Volk, dass bis zu 50.000 Mitglieder zählen kann und können nicht alleine überleben, da sie den sozialen Kontakt zu anderen Bienen benötigen. Das Volk besteht aus einer Königin, den Arbeitsbienen und Drohnen. Während die Arbeiterinnen für das Sammeln, Putzen und Wache halten zuständig sind, besteht die Aufgabe der Drohnen nur darin eine Königin zu befruchten und damit das Überleben des Volkes und der Nachkommen zu sichern.</p>\n<p>Die Honigbiene kann täglich etwa 300 Blüten bestäuben und übernimmt zusammen mit den Wildbienen und anderen bestäubenden Insekten ca. 80 bis 90 Prozent der Bestäubungsarbeit.</p>\n<p><strong>Wildbienen sind Einzelgänger</strong></p>\n<p>Alleine in Europa gibt es mehr als 2.500 unterschiedliche Wildbienenarten, in Deutschland geht man von über 550 Arten aus. Die Nistplätze der Wildbienen befinden sich vor allem unter der Erde, aber auch Totholz, Pflanzenstängel, leere Schneckenhäuser oder von Menschen gebaute Bienenhotels werden genutzt.</p>\n<p>Genau wie die Honigbienen haben auch die Wildbienen eine große Bedeutung für die Bestäubung von Bäumen, Blumen und Nutzpflanzen.</p>\n<p>Während die Honigbiene in einem großen Volk mit vielen anderen Bienen in Bienenstöcken lebt, lebt die Wildbiene als Einzelgänger und nistet vor allem im Boden oder sucht sich Pflanzenhalme oder nutzt Fraßgänge von Käfern im Holz.</p>', '', NULL, '', '', '', '', '', '', 'above', '', NULL, '', '1', '', '', NULL);
INSERT INTO `tl_faq` (`id`, `pid`, `sorting`, `tstamp`, `question`, `alias`, `author`, `answer`, `addImage`, `singleSRC`, `alt`, `size`, `imagemargin`, `imageUrl`, `fullsize`, `caption`, `floating`, `addEnclosure`, `enclosure`, `noComments`, `published`, `overwriteMeta`, `imageTitle`, `orderEnclosure`) VALUES(2, 1, 256, 1568210147, 'Wie kann man Bienen schützen?', 'wie-kann-man-bienen-schutzen', 1, '<p>Es gibt viele Faktoren, die für das Bienensterben verantwortlich sind. Zum einen finden Bienen nicht mehr genug Nahrung, da in der Landschaft immer mehr Monokulturen angebaut werden, die nur für eine kurze Zeit blühen. Pestizide, der Klimawandel und natürliche Feinde, die teilweise aus anderen Regionen eingeschleppt wurden, bedrohen ebenfalls den Bestand der Bienen.</p>\n<p>Würde es keine Bienen mehr geben, hätte das große Folgen für die Umwelt, da sie für den größten Teil der Bestäubung der Pflanzen zuständig sind. Es würde nicht nur keinen Honig mehr geben, sondern es würde auch viel weniger Obst und Gemüse geben.</p>\n<p>Man kann den Bienen ganz einfach helfen, indem man bienenfreundliche Blumen anpflanzt und ihnen so genug Nahrung bietet oder auch Honig von örtlichen Imkern bezieht und sie damit unterstützt.</p>\n<p><strong>Zusammengefasste Liste, wie man Bienen helfen kann:</strong></p>\n<ul>\n<li>bienenfreundliche Blumen anpflanzen</li>\n<li>den örtlichen Imker unterstützen</li>\n<li>Aufstellen von Nisthilfen</li>\n<li>Urban Beekeeping</li>\n<li>Bienenpatenschaft</li>\n<li>Nutzen von saisonalen Lebensmittel aus regionaler ökologischer Landwirtschaft</li>\n<li>Honiggläser immer ausspülen - zum Schutz vor Bienenkrankheiten</li>\n<li>auf Pestizide verzichten</li>\n</ul>', '', NULL, '', '', '', '', '', '', 'above', '', NULL, '', '1', '', '', NULL);
INSERT INTO `tl_faq` (`id`, `pid`, `sorting`, `tstamp`, `question`, `alias`, `author`, `answer`, `addImage`, `singleSRC`, `alt`, `size`, `imagemargin`, `imageUrl`, `fullsize`, `caption`, `floating`, `addEnclosure`, `enclosure`, `noComments`, `published`, `overwriteMeta`, `imageTitle`, `orderEnclosure`) VALUES(3, 1, 384, 1568210169, 'Was ist Urban Beekeeping?', 'was-ist-urban-beekeeping', 1, '<p>Urban Beekeeping, das Imkern in Großstädten, ist ein neuer Trend und ebenfalls eine gute Sache um Bienen zu schützen. Alleine in Berlin gibt es z. B. über 500 Großstadtimker.</p>\n<p>Es ist nicht so, dass es in den Städten keinen geeigneten Lebensraum für Bienen gibt. Parkanlegen, Gärten, Alleen oder auch Balkonpflanzen können für genug Vielfalt und geeigneten Lebensraum sorgen. Zudem ist es in der Stadt durchschnittlich 2 bis 3 Grad wärmer als im Umland, was der Biene gute Voraussetzungen zum Leben schafft.</p>', '', NULL, '', '', '', '', '', '', 'above', '', NULL, '', '1', '', '', NULL);
INSERT INTO `tl_faq` (`id`, `pid`, `sorting`, `tstamp`, `question`, `alias`, `author`, `answer`, `addImage`, `singleSRC`, `alt`, `size`, `imagemargin`, `imageUrl`, `fullsize`, `caption`, `floating`, `addEnclosure`, `enclosure`, `noComments`, `published`, `overwriteMeta`, `imageTitle`, `orderEnclosure`) VALUES(4, 1, 512, 1568210208, 'Wie viele Blüten kann eine Biene am Tag bestäuben?', 'wie-viele-bluten-kann-eine-biene-am-tag-bestauben', 1, '<p>Die Honigbiene kann täglich etwa <strong>300 Blüten</strong> bestäuben und übernimmt zusammen mit den Wildbienen und anderen bestäubenden Insekten ca. 80 bis 90 Prozent der Bestäubungsarbeit.</p>\n<p>Hummeln schaffen sogar rund <strong>1.000 Blüten</strong> an einem Tag. Während die Honigbiene bei kühleren Temperaturen noch ruht, ist die Wildbiene auch schon bei schlechteren Wetterbedingungen und kühleren Temperaturen unterwegs und beginnt mit der Bestäubung.</p>', '', NULL, '', '', '', '', '', '', 'above', '', NULL, '', '1', '', '', NULL);
INSERT INTO `tl_faq` (`id`, `pid`, `sorting`, `tstamp`, `question`, `alias`, `author`, `answer`, `addImage`, `singleSRC`, `alt`, `size`, `imagemargin`, `imageUrl`, `fullsize`, `caption`, `floating`, `addEnclosure`, `enclosure`, `noComments`, `published`, `overwriteMeta`, `imageTitle`, `orderEnclosure`) VALUES(5, 1, 640, 1568210246, 'Wie viele Honigbienenarten gibt es auf der Erde?', 'wie-viele-honigbienenarten-gibt-es-auf-der-erde', 1, '<p>Auf der ganzen Welt gibt es neun Honigbienenarten, wovon nur die westliche Honigbiene in Europa heimisch ist.</p>', '', NULL, '', '', '', '', '', '', 'above', '', NULL, '', '1', '', '', NULL);
INSERT INTO `tl_faq` (`id`, `pid`, `sorting`, `tstamp`, `question`, `alias`, `author`, `answer`, `addImage`, `singleSRC`, `alt`, `size`, `imagemargin`, `imageUrl`, `fullsize`, `caption`, `floating`, `addEnclosure`, `enclosure`, `noComments`, `published`, `overwriteMeta`, `imageTitle`, `orderEnclosure`) VALUES(6, 1, 768, 1568210316, 'Wie viele Wildbienenarten gibt es auf der Erde?', 'wie-viele-wildbienenarten-gibt-es-auf-der-erde', 1, '<p>Es gibt auf der Welt mehr als <strong>30.000</strong> unterschiedliche Wildbienenarten. Alleine in Europa gibt es mehr als<strong> 2.500</strong> Arten, in Deutschland geht man von über <strong>550 Arten</strong> aus.</p>', '', NULL, '', '', '', '', '', '', 'above', '', NULL, '', '1', '', '', NULL);
